    <span style="color:Blue;">using</span> (<span style="color:Blue;">var</span> g = Graphics.FromImage(bitmap))
    {
        g.SmoothingMode = System.Drawing.Drawing2D.SmoothingMode.HighQuality;
        g.Clear(Color.Black);

        renderer.RenderGeometry(g, zoom);

        <span style="color:Blue;">this</span>.Invalidate();
    }
}

