<div style="color:Black;background-color:White;"><pre>
<span style="color:Green;">// Create some input mesh.</span>
<span style="color:Blue;">var</span> mesh1 = GenericMesher.StructuredMesh(<span style="color:Blue;">new</span> Rectangle(-2.0, -2.0, 4.0, 4.0), 10, 10);

<span style="color:Green;">// Add some z values.</span>
<span style="color:Blue;">foreach</span> (<span style="color:Blue;">var</span> p <span style="color:Blue;">in</span> mesh1.Vertices)
{
    p.Z = Math.Exp(-(p.X * p.X + p.Y * p.Y));
}

<span style="color:Green;">// Polygon to store the input mesh points and segments.</span>
<span style="color:Blue;">var</span> poly = <span style="color:Blue;">new</span> Polygon();

poly.Points.AddRange(mesh1.Vertices);
poly.Segments.AddRange(mesh1.Segments);

<span style="color:Green;">// Construct new mesh using input mesh data.</span>
<span style="color:Blue;">var</span> mesh2 = Converter.ToMesh(poly, mesh1.Triangles.ToArray());
